import logging
import sys

formatter = (
    "{'timestamp':'%(asctime)s', 'path': '%(name)s', 'file': '%(filename)s:%(lineno)d'"
    "'process': %(process)d, 'level': '%(levelname)s', "
    "'message': '%(message)s'}"
)
logging.basicConfig(
    stream=sys.stdout, level=logging.DEBUG, format=formatter
)