import threading


class _Context:
    def __init__(self):
        """
        Sets a new thread context
        """
        self._thread_local = threading.local()

    @property
    def request_id(self):
        """
        Gets the request ID
        """
        return getattr(self._thread_local, "request_id", None)

    @request_id.setter
    def request_id(self, value):
        """
        Sets the request ID
        Args:
            value: The request ID
        """
        self._thread_local.request_id = value


ctx = _Context()
