from pydantic import BaseSettings

DEFAULT_ENV_FILE: str = ".env"


class Config(BaseSettings):
    """
    This config class is to be extended for the service config class. This class
    loads from .env/environment into the service config class fields. This class
    now uses Pydantic instead of rolling our own, so look at the pydantic docs
    for more info.

    Example::
        class MyConfig(Config):
            my_var1: int = 1212
            my_var2: int = 0
            my_var3: Optional[bool] = False
            my_var4: str = "default"
            my_var5: float = 0.
        my_config = MyConfig()
    """

    def __init__(self, env_file: str = DEFAULT_ENV_FILE):
        """
        Args:
            env_file: What env file to load
        """
        super().__init__(_env_file=env_file, _env_file_encoding="utf-8")
