from pydantic.main import BaseModel


class DBConfig(BaseModel):
    """
    Config to be inherited for easy db configuration
    """

    db_username: str
    db_password: str
    db_host: str
    db_port: int = 5432
    db_database: str
    db_pool_size: int = 100
    db_debug: bool = False

    class Config:
        env_prefix = "db_"  # defaults to no prefix, i.e. ""
        fields = {
            "auth_key": {
                "env": "my_auth_key",
            },
            "redis_dsn": {"env": ["service_redis_dsn", "redis_url"]},
        }
