"""
Attributes:
    DEFAULT_NA_REP (str): The default N/A representation string
"""
from string import Formatter

DEFAULT_NA_REP = "NA"


class NaReppingFormatter(Formatter):
    """Custom formatter that outputs a custom string when a None value is being formatted."""

    def __init__(self, na_rep="NA"):
        super().__init__()

        self.na_rep = na_rep

    def format_field(self, value, format_spec):
        if value is None:
            return self.na_rep
        else:
            return format_spec.format(value)

    def get_formatter(self, format_spec):
        def wrapper(value):
            return self.format_field(value, format_spec)

        return wrapper
