from pydantic.main import BaseModel


class PgSQLConfig(BaseModel):
    """
    Holds the config for a PgSQL instance
    """

    username: str = None
    password: str = None
    host: str = None
    port: int = None
    database: str = None
    pool_size: int = 100
    debug: bool = False

    def __init__(self, config: BaseModel):
        super().__init__(**config.dict())

    class Config:
        fields = {
            "username": {"alias": "db_username"},
            "password": {"alias": "db_password"},
            "host": {"alias": "db_host"},
            "port": {"alias": "db_port"},
            "database": {"alias": "db_database"},
            "pool_size": {"alias": "db_pool_size"},
            "debug": {"alias": "db_debug"},
        }
